import React, { useState } from 'react';
import { format, startOfMonth, endOfMonth, eachDayOfInterval, isSameMonth, isSameDay } from 'date-fns';

function MonthlyView({ selectedDate, setSelectedDate, events, setEvents }) {
  const [showAddTaskModal, setShowAddTaskModal] = useState(false);

  // Get all days in the current month
  const monthStart = startOfMonth(selectedDate);
  const monthEnd = endOfMonth(selectedDate);
  const daysInMonth = eachDayOfInterval({ start: monthStart, end: monthEnd });

  // Filter events for the current month
  const monthEvents = events.filter(event => {
    const eventDate = new Date(event.date);
    return isSameMonth(eventDate, selectedDate);
  });

  const handlePrevMonth = () => {
    const newDate = new Date(selectedDate.setMonth(selectedDate.getMonth() - 1));
    setSelectedDate(new Date(newDate));
  };

  const handleNextMonth = () => {
    const newDate = new Date(selectedDate.setMonth(selectedDate.getMonth() + 1));
    setSelectedDate(new Date(newDate));
  };

  const handleDayClick = (day) => {
    setSelectedDate(day);
  };

  const handleDeleteEvent = (eventId) => {
    const updatedEvents = events.filter(event => event.id !== eventId);
    setEvents(updatedEvents);
  };

  const handleEditEvent = (event) => {
    // Implement edit functionality
  };

  const getDayEvents = (day) => {
    return events.filter(event => isSameDay(new Date(event.date), day));
  };

  return (
    <div className="monthly-view">
      <div className="date-navigation">
        <button onClick={handlePrevMonth}>Previous Month</button>
        <h2>{format(selectedDate, 'MMMM yyyy')}</h2>
        <button onClick={handleNextMonth}>Next Month</button>
      </div>

      <div className="month-grid">
        <div className="weekday-header">
          {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
            <div key={day} className="weekday-name">{day}</div>
          ))}
        </div>

        <div className="days-grid">
          {daysInMonth.map((day, index) => {
            const dayEvents = getDayEvents(day);
            return (
              <div
                key={day.toString()}
                className={`day-cell ${isSameDay(day, new Date()) ? 'today' : ''}`}
                onClick={() => handleDayClick(day)}
              >
                <div className="day-number">{format(day, 'd')}</div>
                <div className="day-events">
                  {dayEvents.map(event => (
                    <div
                      key={event.id}
                      className={`event-pill priority-${event.priority.toLowerCase()}`}
                      title={event.title}
                    >
                      <span className="event-title">{event.title}</span>
                      <div className="event-actions">
                        <button
                          className="edit-button"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleEditEvent(event);
                          }}
                        >
                          Edit
                        </button>
                        <button
                          className="delete-button"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleDeleteEvent(event.id);
                          }}
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default MonthlyView;
