import React from 'react';

function RecurringOptionsModal({ isOpen, onClose, onSave, recurringOptions, setRecurringOptions }) {
  if (!isOpen) return null;

  const handleSave = () => {
    onSave(recurringOptions);
    onClose();
  };

  const getPreviewText = () => {
    const frequencyMap = {
      daily: 'day',
      weekly: 'week',
      monthly: 'month',
      yearly: 'year'
    };

    const weekDaysMap = {
      0: 'Sunday',
      1: 'Monday',
      2: 'Tuesday',
      3: 'Wednesday',
      4: 'Thursday',
      5: 'Friday',
      6: 'Saturday'
    };

    let text = 'This task will repeat ';

    if (recurringOptions.frequency === 'daily') {
      text += `every ${recurringOptions.interval} ${frequencyMap.daily}`;
    } else if (recurringOptions.frequency === 'weekly') {
      const selectedDays = recurringOptions.weekDays
        .map(day => weekDaysMap[day])
        .join(', ');
      text += `every ${recurringOptions.interval} ${frequencyMap.weekly} on ${selectedDays}`;
    } else if (recurringOptions.frequency === 'monthly') {
      text += `every ${recurringOptions.interval} ${frequencyMap.monthly}`;
    } else if (recurringOptions.frequency === 'yearly') {
      text += `every ${recurringOptions.interval} ${frequencyMap.yearly}`;
    }

    if (recurringOptions.endDate) {
      const endDate = new Date(recurringOptions.endDate);
      text += ` until ${endDate.toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' })}`;
    }

    return text;
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Recurring Options</h2>
        <div className="input-row">
          <label>Frequency:</label>
          <select
            value={recurringOptions.frequency}
            onChange={(e) => setRecurringOptions({ ...recurringOptions, frequency: e.target.value })}
          >
            <option value="daily">Daily</option>
            <option value="weekly">Weekly</option>
            <option value="monthly">Monthly</option>
            <option value="yearly">Yearly</option>
          </select>
        </div>
        {recurringOptions.frequency === 'weekly' && (
          <div className="input-row">
            <label>Days of Week:</label>
            <div className="weekday-checkboxes">
              {['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'].map((day, index) => (
                <label key={day}>
                  <input
                    type="checkbox"
                    checked={recurringOptions.weekDays.includes(index)}
                    onChange={(e) => {
                      const newWeekDays = e.target.checked
                        ? [...recurringOptions.weekDays, index]
                        : recurringOptions.weekDays.filter(d => d !== index);
                      setRecurringOptions({ ...recurringOptions, weekDays: newWeekDays });
                    }}
                  />
                  {day.slice(0, 3)}
                </label>
              ))}
            </div>
          </div>
        )}
        <div className="input-row">
          <label>End Date:</label>
          <input
            type="date"
            value={recurringOptions.endDate}
            onChange={(e) => setRecurringOptions({ ...recurringOptions, endDate: e.target.value })}
          />
        </div>
        <div className="input-row">
          <label>Interval:</label>
          <input
            type="number"
            min="1"
            value={recurringOptions.interval}
            onChange={(e) => setRecurringOptions({ ...recurringOptions, interval: parseInt(e.target.value) })}
          />
          <span className="interval-text">
            {recurringOptions.frequency === 'daily' && 'days'}
            {recurringOptions.frequency === 'weekly' && 'weeks'}
            {recurringOptions.frequency === 'monthly' && 'months'}
            {recurringOptions.frequency === 'yearly' && 'years'}
          </span>
        </div>
        <div className="preview-section">
          <h3>Preview</h3>
          <p className="preview-text">{getPreviewText()}</p>
        </div>
        <div className="modal-buttons">
          <button className="save-btn" onClick={handleSave}>Save</button>
          <button className="cancel-btn" onClick={onClose}>Cancel</button>
        </div>
      </div>
    </div>
  );
}

export default RecurringOptionsModal;
