import React, { useState, useEffect, useRef } from 'react';

function RecurringOptionsModal({ isOpen, onClose, onSave, initialOptions }) {
  const [options, setOptions] = useState(initialOptions || {
    frequency: 'daily',
    weekDays: [],
    endDate: '',
    interval: 1
  });
  const modalRef = useRef(null);
  const firstFocusableElementRef = useRef(null);
  const lastFocusableElementRef = useRef(null);

  const frequencies = [
    { value: 'daily', label: 'Daily' },
    { value: 'weekly', label: 'Weekly' },
    { value: 'monthly', label: 'Monthly' },
    { value: 'yearly', label: 'Yearly' }
  ];

  const weekDays = [
    { value: 0, label: 'Sunday' },
    { value: 1, label: 'Monday' },
    { value: 2, label: 'Tuesday' },
    { value: 3, label: 'Wednesday' },
    { value: 4, label: 'Thursday' },
    { value: 5, label: 'Friday' },
    { value: 6, label: 'Saturday' }
  ];

  useEffect(() => {
    if (isOpen) {
      // Focus the first element when modal opens
      firstFocusableElementRef.current?.focus();

      // Add keyboard event listener for modal
      const handleKeyDown = (e) => {
        if (e.key === 'Escape') {
          onClose();
        } else if (e.key === 'Tab') {
          if (e.shiftKey) {
            if (document.activeElement === firstFocusableElementRef.current) {
              e.preventDefault();
              lastFocusableElementRef.current?.focus();
            }
          } else {
            if (document.activeElement === lastFocusableElementRef.current) {
              e.preventDefault();
              firstFocusableElementRef.current?.focus();
            }
          }
        }
      };

      document.addEventListener('keydown', handleKeyDown);
      return () => document.removeEventListener('keydown', handleKeyDown);
    }
  }, [isOpen, onClose]);

  const handleFrequencyChange = (e) => {
    setOptions({ ...options, frequency: e.target.value });
  };

  const handleWeekDayToggle = (day) => {
    setOptions({
      ...options,
      weekDays: options.weekDays.includes(day)
        ? options.weekDays.filter(d => d !== day)
        : [...options.weekDays, day]
    });
  };

  const handleIntervalChange = (e) => {
    const value = Math.max(1, parseInt(e.target.value) || 1);
    setOptions({ ...options, interval: value });
  };

  const handleEndDateChange = (e) => {
    setOptions({ ...options, endDate: e.target.value });
  };

  const handleSave = () => {
    onSave(options);
    onClose();
  };

  const getPreviewMessage = () => {
    if (!options.endDate) return '';

    const frequency = options.frequency;
    const interval = options.interval > 1 ? `every ${options.interval} ${frequency}s` : `every ${frequency}`;

    // Sort weekdays in chronological order
    const sortedWeekDays = [...options.weekDays].sort((a, b) => a - b);
    const weekDaysText = sortedWeekDays.length > 0 
      ? ` on ${sortedWeekDays.map(day => ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][day]).join(', ')}`
      : '';

    const endDate = new Date(options.endDate).toLocaleDateString('en-US', { 
      month: 'long',
      day: 'numeric',
      year: 'numeric'
    });

    return `This task will repeat ${interval}${weekDaysText} until ${endDate}`;
  };

  if (!isOpen) return null;

  return (
    <div 
      className="modal-overlay recurring-modal-overlay"
      role="dialog"
      aria-modal="true"
      aria-labelledby="modal-title"
      ref={modalRef}
    >
      <div className="modal-content">
        <h2 id="modal-title">Set Recurring Options</h2>
        <div className="form-group">
          <label>Frequency</label>
          <select
            value={options.frequency}
            onChange={handleFrequencyChange}
            aria-label="Select frequency of recurrence"
            ref={firstFocusableElementRef}
          >
            {frequencies.map(freq => (
              <option key={freq.value} value={freq.value}>
                {freq.label}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Repeat every</label>
          <input
            type="number"
            min="1"
            value={options.interval}
            onChange={handleIntervalChange}
            aria-label="Enter interval number"
          />
          <span className="interval-label">
            {options.frequency === 'daily' ? 'day(s)' :
             options.frequency === 'weekly' ? 'week(s)' :
             options.frequency === 'monthly' ? 'month(s)' : 'year(s)'}
          </span>
        </div>

        {options.frequency === 'weekly' && (
          <div className="form-group">
            <label>Repeat on</label>
            <div className="week-days">
              {weekDays.map(day => (
                <button
                  key={day.value}
                  className={`week-day ${options.weekDays.includes(day.value) ? 'selected' : ''}`}
                  onClick={() => handleWeekDayToggle(day.value)}
                >
                  {day.label[0]}
                </button>
              ))}
            </div>
          </div>
        )}

        <div className="form-group">
          <label>End Date</label>
          <input
            type="date"
            value={options.endDate}
            onChange={handleEndDateChange}
            min={new Date().toISOString().split('T')[0]}
            aria-label="Select end date"
          />
        </div>

        <div 
          className="preview-message"
          role="status"
          aria-live="polite"
        >
          {getPreviewMessage()}
        </div>

        <div className="modal-actions">
          <button 
            onClick={handleSave}
            aria-label="Save recurring options"
          >
            Save
          </button>
          <button 
            onClick={onClose}
            aria-label="Cancel and close modal"
            ref={lastFocusableElementRef}
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
}

export default RecurringOptionsModal;
