import React, { useMemo, useState, useEffect } from 'react';
import RecurringOptionsModal from './RecurringOptionsModal';

const getLocalDateString = (date) => {
  const d = new Date(date);
  const year = d.getFullYear();
  const month = String(d.getMonth() + 1).padStart(2, '0');
  const day = String(d.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};

function DayDetails({ selectedDate, setSelectedDate, events, setEvents }) {
  // State management
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    startTime: '',
    endTime: '',
    category: 'Work',
    priority: 'Medium',
    recurring: false,
  });

  const [recurringOptions, setRecurringOptions] = useState({
    frequency: 'daily',
    weekDays: [],
    endDate: '',
    interval: 1
  });

  const [errors, setErrors] = useState({});
  const [showUndo, setShowUndo] = useState(false);
  const [lastAction, setLastAction] = useState(null);
  const [recurringModalOpen, setRecurringModalOpen] = useState(false);
  const [editModalOpen, setEditModalOpen] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  // Constants
  const categories = useMemo(() => ['Work', 'Personal', 'School'], []);
  const priorities = ['Low', 'Medium', 'High', 'Critical'];

  const months = useMemo(() => [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ], []);

  const years = useMemo(() => {
    const currentYear = new Date().getFullYear();
    return Array.from({ length: 20 }, (_, i) => currentYear - 10 + i);
  }, []);

  // Helper functions
  const validateTimeInputs = (startTime, endTime) => {
    const errors = {};
    if (!startTime) {
      errors.startTime = 'Start time is required';
    }
    if (!endTime) {
      errors.endTime = 'End time is required';
    }
    if (startTime && endTime) {
      const start = new Date(`2000-01-01T${startTime}`);
      const end = new Date(`2000-01-01T${endTime}`);
      if (end <= start) {
        errors.endTime = 'End time must be after start time';
      }
    }
    return errors;
  };

  const checkForOverlappingEvents = (startTime, endTime, taskId = null) => {
    if (!startTime || !endTime) return false;

    const newStart = new Date(`2000-01-01T${startTime}`);
    const newEnd = new Date(`2000-01-01T${endTime}`);

    return events.some(task => {
      if (taskId && task.taskId === taskId) return false;
      if (task.date !== getLocalDateString(selectedDate)) return false;

      const taskStart = new Date(`2000-01-01T${task.startTime}`);
      const taskEnd = new Date(`2000-01-01T${task.endTime}`);
      return (newStart < taskEnd && newEnd > taskStart);
    });
  };

  const generateRecurringDates = (startDate, options) => {
    const dates = [];
    const start = new Date(startDate);
    const end = new Date(options.endDate);
    let currentDate = new Date(start);

    while (currentDate <= end) {
      if (options.frequency === 'daily') {
        dates.push(new Date(currentDate));
        currentDate.setDate(currentDate.getDate() + options.interval);
      } else if (options.frequency === 'weekly') {
        if (options.weekDays.includes(currentDate.getDay())) {
          dates.push(new Date(currentDate));
        }
        currentDate.setDate(currentDate.getDate() + 1);
        if (currentDate.getDay() === 0) {
          currentDate.setDate(currentDate.getDate() + (options.interval - 1) * 7);
        }
      } else if (options.frequency === 'monthly') {
        dates.push(new Date(currentDate));
        currentDate.setMonth(currentDate.getMonth() + options.interval);
      } else if (options.frequency === 'yearly') {
        dates.push(new Date(currentDate));
        currentDate.setFullYear(currentDate.getFullYear() + options.interval);
      }
    }

    return dates;
  };

  // Add this helper function for 12-hour time format
  const formatTime12Hour = (time) => {
    if (!time) return '';
    const [hours, minutes] = time.split(':').map(Number);
    const period = hours >= 12 ? 'PM' : 'AM';
    const adjustedHours = hours % 12 || 12;
    return `${adjustedHours}:${minutes.toString().padStart(2, '0')} ${period}`;
  };

  // Event handlers
  const handleInputChange = (field, value) => {
    setNewTask(prev => ({ ...prev, [field]: value }));
    // Clear error for this field if it exists
    if (errors[field]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[field];
        return newErrors;
      });
    }
  };

  const handleClearForm = () => {
    setNewTask({
      title: '',
      description: '',
      startTime: '',
      endTime: '',
      category: 'Work',
      priority: 'Medium',
      recurring: false,
    });
    setErrors({});
    setRecurringOptions({
      frequency: 'daily',
      weekDays: [],
      endDate: '',
      interval: 1
    });
  };

  const handleAddTask = () => {
    // Validate required fields
    if (!newTask.title.trim()) {
      setErrors({ title: 'Title is required' });
      return;
    }

    const timeErrors = validateTimeInputs(newTask.startTime, newTask.endTime);
    if (Object.keys(timeErrors).length > 0) {
      setErrors(timeErrors);
      return;
    }

    // Only check for overlapping events if we have valid times
    if (newTask.startTime && newTask.endTime) {
      const hasOverlap = checkForOverlappingEvents(newTask.startTime, newTask.endTime);
      if (hasOverlap) {
        setErrors({ overlap: 'This event overlaps with an existing event' });
        return;
      }
    }

    setErrors({});
    const taskData = {
      taskId: Date.now().toString(),
      title: newTask.title.trim(),
      description: newTask.description.trim(),
      startTime: newTask.startTime,
      endTime: newTask.endTime,
      category: newTask.category,
      priority: newTask.priority,
      recurring: newTask.recurring,
      recurringOptions: newTask.recurring ? recurringOptions : null,
      metadata: { createdBy: 'CurrentUser', lastUpdated: new Date().toISOString() },
      date: getLocalDateString(selectedDate),
    };

    let tasksToAdd = [taskData];

    if (taskData.recurring && taskData.recurringOptions) {
      const recurringDates = generateRecurringDates(
        new Date(`${taskData.date}T${taskData.startTime}`),
        taskData.recurringOptions
      );

      tasksToAdd = recurringDates.map((date, index) => ({
        ...taskData,
        taskId: `${taskData.taskId}-${index}`,
        date: getLocalDateString(date),
        startTime: date.toTimeString().slice(0, 5),
        endTime: new Date(date.getTime() + (new Date(`1970/01/01 ${taskData.endTime}`).getTime() - new Date(`1970/01/01 ${taskData.startTime}`).getTime())).toTimeString().slice(0, 5),
      }));
    }

    setLastAction({
      type: 'add',
      tasks: tasksToAdd,
      previousEvents: [...events]
    });
    setEvents(prevEvents => [...prevEvents, ...tasksToAdd]);
    setShowUndo(true);
    setTimeout(() => setShowUndo(false), 5000);

    handleClearForm();
  };

  const handleRecurringChange = (checked) => {
    setNewTask(prev => ({ ...prev, recurring: checked }));
    if (checked) {
      setRecurringModalOpen(true);
    }
  };

  const handleRecurringOptionsSave = (options) => {
    setRecurringOptions(options);
    setRecurringModalOpen(false);
    // Update the newTask with recurring options
    setNewTask(prev => ({
      ...prev,
      recurringOptions: options
    }));
  };

  const handleEditTask = (task) => {
    setSelectedTask(task);
    setEditModalOpen(true);
  };

  const handleDeleteTask = (taskId) => {
    const task = events.find(t => t.taskId === taskId);
    if (task) {
      setLastAction({
        type: 'delete',
        taskId: taskId,
        previousEvents: [...events]
      });
      setEvents(prevEvents => prevEvents.filter(t => t.taskId !== taskId));
      setShowUndo(true);
      setTimeout(() => setShowUndo(false), 5000);
    }
  };

  const handleUndo = () => {
    if (lastAction) {
      if (lastAction.type === 'add') {
        setEvents(lastAction.previousEvents);
      } else if (lastAction.type === 'delete') {
        setEvents(prevEvents => [...prevEvents, ...lastAction.previousEvents.filter(t => t.taskId === lastAction.taskId)]);
      }
      setLastAction(null);
      setShowUndo(false);
    }
  };

  // Filter tasks for the current day
  const dayTasks = useMemo(() => {
    return events
      .filter(task => task.date === getLocalDateString(selectedDate))
      .sort((a, b) => {
        const timeA = new Date(`2000-01-01T${a.startTime}`).getTime();
        const timeB = new Date(`2000-01-01T${b.startTime}`).getTime();
        return timeA - timeB;
      });
  }, [events, selectedDate]);

  // Filter tasks based on search query
  const filteredTasks = useMemo(() => {
    if (!searchQuery.trim()) return dayTasks;
    const query = searchQuery.toLowerCase().trim();
    return dayTasks.filter(task => 
      task.title.toLowerCase().includes(query) ||
      (task.description && task.description.toLowerCase().includes(query)) ||
      task.category.toLowerCase().includes(query)
    );
  }, [dayTasks, searchQuery]);

  return (
    <div className="day-details">
      <h2>Details for {selectedDate.toLocaleDateString('en-US', { weekday: 'long', month: 'long', day: 'numeric' })}</h2>

      <div className="date-navigation">
        <button onClick={() => setSelectedDate(new Date(selectedDate.setDate(selectedDate.getDate() - 1)))}>Previous Day</button>
        <button onClick={() => setSelectedDate(new Date())}>Today</button>
        <button onClick={() => setSelectedDate(new Date(selectedDate.setDate(selectedDate.getDate() + 1)))}>Next Day</button>
        <div className="date-selector">
          <select
            value={selectedDate.getFullYear()}
            onChange={(e) => setSelectedDate(new Date(selectedDate.setFullYear(parseInt(e.target.value))))}
          >
            {years.map(year => (
              <option key={year} value={year}>{year}</option>
            ))}
          </select>
          <select
            value={selectedDate.getMonth()}
            onChange={(e) => setSelectedDate(new Date(selectedDate.setMonth(parseInt(e.target.value))))}
          >
            {months.map((month, index) => (
              <option key={month} value={index}>{month}</option>
            ))}
          </select>
          <select
            value={selectedDate.getDate()}
            onChange={(e) => setSelectedDate(new Date(selectedDate.setDate(parseInt(e.target.value))))}
          >
            {Array.from({ length: new Date(selectedDate.getFullYear(), selectedDate.getMonth() + 1, 0).getDate() }, (_, i) => i + 1).map(day => (
              <option key={day} value={day}>{day}</option>
            ))}
          </select>
        </div>
      </div>

      <div className="details-content">
        <div className="timeline-section">
          <h3>Today's Timeline</h3>
          <div className="timeline">
            {filteredTasks.map((task) => (
              <div key={task.taskId} className="timeline-item">
                <div className="task-content">
                  <div className="task-title">
                    {task.title}
                    {task.recurring && <span className="recurring-badge">ðŸ”„</span>}
                  </div>
                  <div className="task-time">
                    {task.startTime} - {task.endTime}
                  </div>
                  <div className="task-category">
                    {task.category}
                  </div>
                  <div className="task-priority">
                    Priority: {task.priority}
                  </div>
                  {task.description && (
                    <div className="task-description">
                      {task.description}
                    </div>
                  )}
                </div>
                <div className="task-actions">
                  <button className="edit-button" onClick={() => handleEditTask(task)}>Edit</button>
                  <button className="delete-button" onClick={() => handleDeleteTask(task.taskId)}>Delete</button>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="right-section">
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search tasks..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
          </div>

          <div className="task-form">
            <h3>Add New Task</h3>
            <div className="form-group">
              <label>Task Title:</label>
              <input
                type="text"
                value={newTask.title}
                onChange={(e) => handleInputChange('title', e.target.value)}
                placeholder="Enter task title"
                className={errors.title ? 'error' : ''}
              />
              {errors.title && <span className="error-message">{errors.title}</span>}
            </div>
            <div className="form-group">
              <label>Task Description:</label>
              <textarea
                value={newTask.description}
                onChange={(e) => handleInputChange('description', e.target.value)}
                placeholder="Enter task description"
              />
            </div>
            <div className="form-group">
              <label>Start Time:</label>
              <input
                type="time"
                value={newTask.startTime}
                onChange={(e) => handleInputChange('startTime', e.target.value)}
                className={errors.startTime ? 'error' : ''}
              />
              {errors.startTime && <span className="error-message">{errors.startTime}</span>}
            </div>
            <div className="form-group">
              <label>End Time:</label>
              <input
                type="time"
                value={newTask.endTime}
                onChange={(e) => handleInputChange('endTime', e.target.value)}
                className={errors.endTime ? 'error' : ''}
              />
              {errors.endTime && <span className="error-message">{errors.endTime}</span>}
            </div>
            <div className="form-group">
              <label>Category:</label>
              <select value={newTask.category} onChange={(e) => handleInputChange('category', e.target.value)}>
                {categories.map(cat => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label>Priority:</label>
              <select value={newTask.priority} onChange={(e) => handleInputChange('priority', e.target.value)}>
                {priorities.map(p => (
                  <option key={p} value={p}>{p}</option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label>
                <input
                  type="checkbox"
                  checked={newTask.recurring}
                  onChange={(e) => handleRecurringChange(e.target.checked)}
                />
                Recurring Task
              </label>
            </div>
            {errors.overlap && <div className="error-message">{errors.overlap}</div>}
            <div className="form-actions">
              <button onClick={handleClearForm}>Clear Form</button>
              <button onClick={handleAddTask}>Add Task</button>
            </div>
          </div>
        </div>
      </div>

      {showUndo && (
        <div className="undo-notification">
          <button onClick={handleUndo}>Undo Last Action</button>
        </div>
      )}

      {recurringModalOpen && (
        <RecurringOptionsModal
          onSave={handleRecurringOptionsSave}
          onClose={() => setRecurringModalOpen(false)}
          initialOptions={recurringOptions}
        />
      )}

      {editModalOpen && selectedTask && (
        <div className="modal">
          <div className="modal-content">
            <h3>Edit Task</h3>
            <div className="form-group">
              <label>Title:</label>
              <input
                type="text"
                value={selectedTask.title}
                onChange={(e) => setSelectedTask({ ...selectedTask, title: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>Description:</label>
              <textarea
                value={selectedTask.description}
                onChange={(e) => setSelectedTask({ ...selectedTask, description: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>Start Time:</label>
              <input
                type="time"
                value={selectedTask.startTime}
                onChange={(e) => setSelectedTask({ ...selectedTask, startTime: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>End Time:</label>
              <input
                type="time"
                value={selectedTask.endTime}
                onChange={(e) => setSelectedTask({ ...selectedTask, endTime: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>Category:</label>
              <select
                value={selectedTask.category}
                onChange={(e) => setSelectedTask({ ...selectedTask, category: e.target.value })}
              >
                {categories.map(cat => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label>Priority:</label>
              <select
                value={selectedTask.priority}
                onChange={(e) => setSelectedTask({ ...selectedTask, priority: e.target.value })}
              >
                {priorities.map(p => (
                  <option key={p} value={p}>{p}</option>
                ))}
              </select>
            </div>
            <div className="modal-actions">
              <button onClick={() => setEditModalOpen(false)}>Cancel</button>
              <button onClick={() => {
                setEvents(prevEvents => prevEvents.map(task => 
                  task.taskId === selectedTask.taskId ? selectedTask : task
                ));
                setEditModalOpen(false);
              }}>Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default DayDetails;
